// Create function for computing the action of A on x (y = Ax)<br>    std::function&lt;void(la::Vector&lt;T&gt;&amp;, la::Vector&lt;T&gt;&amp;)&gt; action<br>        = [&amp;M, &amp;ui, &amp;bc, &amp;coeff, &amp;constants](la::Vector&lt;T&gt;&amp; x, la::Vector&lt;T&gt;&amp; y)<br>    {<br>      // Zero y<br>      y.set(0.0);<br><br>      // Update coefficient ui (just copy data from x to ui)<br>      std::copy(x.array().begin(), x.array().end(),<br>                ui-&gt;x()-&gt;mutable_array().begin());<br><br>      // Compute action of A on x<br>      fem::pack_coefficients(*M, coeff);<br>      fem::assemble_vector(y.mutable_array(), *M, xtl::span&lt;const T&gt;(constants),<br>                           fem::make_coefficients_span(coeff));<br><br>      // Set BC dofs to zero (effectively zeroes rows of A)<br>      fem::set_bc(y.mutable_array(), {bc}, 0.0);<br><br>      // Accumuate ghost values<br>      y.scatter_rev(common::IndexMap::Mode::add);<br><br>      // Update ghost values<br>      y.scatter_fwd();<br>    };
